mapper.update:
update art_sell_tb
set art_time = sysdate+5/(24*60*60)
where art_no=#{artNo}

update auction_tb
set bid_count= bid_count+1,bid_top=(select mem_name from member_tb where mem_no=#{memNo})
where art_no=#{artNo}

mapper.select:
select art_no, round((art_time-sysdate)*24*60*60) art_time
from art_sell_tb
where art_no=#{artNo}
