mapper.update:
update artist_point_tb
set pay_point = pay_point+#{payAmount}
where mem_no=#{memNo}

mapper.select:
select mem_no, art_no, art_pic, art_name, auc_start, auc_bid, auc_buy
from art_info_tb
where art_no=#{artNo}

mapper.update:
update art_sell_tb
set art_sell='sold'
where art_no=#{artNo}
